import { collection, getDocs, updateDoc, doc } from "firebase/firestore";
import { firestore } from "../firebase";
import { useState, useEffect } from "react";
import Quagga from "quagga";

export default function Mapdata() {
  const [locations, setLocations] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [showScanner, setShowScanner] = useState(false);

  const fetchLocations = async () => {
    const querySnapshot = await getDocs(collection(firestore, "locations"));
    const locationsData = querySnapshot.docs.map((doc) => ({
      ...doc.data(),
      id: doc.id,
    }));
    setLocations(locationsData);
    console.log(locationsData);
  };

  useEffect(() => {
    fetchLocations();
  }, []);

  const handleClick = (location) => {
    window.location.href = location.lola;
  };

  const handleButtonClick = () => {
    setShowScanner(true);
  };

  useEffect(() => {
    if (showScanner) {
      Quagga.init(
        {
          inputStream: {
            name: "Live",
            type: "LiveStream",
            target: document.querySelector("#scanner-container"),
          },
          decoder: {
            readers: ["ean_reader"],
          },
        },
        function (err) {
          if (err) {
            console.log(err);
            return;
          }
          console.log("Initialization finished. Ready to start");
          Quagga.start();
        }
      );

      Quagga.onDetected((data) => {
        setInputValue(data.codeResult.code);
        Quagga.stop();
        setShowScanner(false);
      });

      return () => {
        Quagga.stop();
      };
    }
  }, [showScanner]);

  const handleUpdateCondition = async (locationId) => {
    const locationRef = doc(firestore, "locations", locationId);
    await updateDoc(locationRef, {
      condition: "full",
    });
    const querySnapshot = await getDocs(collection(firestore, "locations"));
    const locationsData = querySnapshot.docs.map((doc) => ({
      ...doc.data(),
      id: doc.id,
    }));
    setLocations(locationsData);
  };

  return (
    <div className="max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">Locations:</h1>
      <div className="flex items-center mb-4">
        {showScanner ? (
          <div id="scanner-container"></div>
        ) : (
          <button
            onClick={handleButtonClick}
            className="py-2 px-4 bg-blue-500 hover:bg-blue-600 text-white rounded-lg shadow-md"
          >
            Scan QR
          </button>
        )}
        {showScanner && (
          <button
            onClick={() => setShowScanner(false)}
            className="py-2 px-4 bg-red-500 hover:bg-red-600 text-white rounded-lg shadow-md ml-4"
          >
            Cancel
          </button>
        )}
      </div>
      <ul>
        {locations.map((location) => (
          <li
            key={location.id}
            onClick={() => handleClick(location)}
            className="py-2 px-4 rounded-lg shadow-md border border-gray-300 hover:bg-gray-100 cursor-pointer"
          >
            <span className="font-bold">{location.name}</span>{" "}
            {location.condition}
            <button
              onClick={() => handleUpdateCondition(location.id)}
              className="py-1 px-2 ml-2 bg-green-500 hover:bg-green-600 text-white rounded-lg shadow-md"
            >
              Full
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
