import { collection, getDocs, updateDoc, doc } from "firebase/firestore";
import { firestore } from "../firebase";
import { useState, useEffect } from "react";

export default function Mapdata() {
  const [locations, setLocations] = useState([]);
  const [inputValue, setInputValue] = useState("");

  const fetchLocations = async () => {
    const querySnapshot = await getDocs(collection(firestore, "locations"));
    const locationsData = querySnapshot.docs.map((doc) => ({
      ...doc.data(),
      id: doc.id,
    }));
    setLocations(locationsData);
    console.log(locationsData);
  };

  useEffect(() => {
    fetchLocations();
  }, []);

  const handleClick = (location) => {
    window.location.href = location.lola;
  };

  const handleUpdateCondition = async (locationId, condition) => {
    const locationRef = doc(firestore, "locations", locationId);
    await updateDoc(locationRef, {
      condition: condition,
    });
    const updatedLocations = locations.map((location) => {
      if (location.id === locationId) {
        return { ...location, condition: condition };
      }
      return location;
    });
    setLocations(updatedLocations);
  };

  const handleFullButtonClick = async (locationId) => {
    await handleUpdateCondition(locationId, "full");
  };

  return (
    <div className="max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">Locations:</h1>
      <ul>
        {locations.map((location) => (
          <li
            key={location.id}
            onClick={() => handleClick(location)}
            className="py-2 px-4 rounded-lg shadow-md border border-gray-300 hover:bg-gray-100 cursor-pointer"
          >
            <span className="font-bold">{location.name}</span>{" "}
            {location.condition}
            <button
              onClick={() => handleFullButtonClick(location.id)}
              className="py-1 px-2 ml-2 bg-green-500 hover:bg-green-600 text-white rounded-lg shadow-md"
            >
              Full
            </button>
          </li>
        ))}
      </ul>

      <div className="mt-4">
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          className="border border-gray-300 rounded-md py-2 px-4"
          placeholder="Enter location ID"
        />
        <button
          onClick={() => handleUpdateCondition(inputValue, "normal")}
          className="ml-4 py-2 px-4 bg-blue-500 hover:bg-blue-600 text-white rounded-lg shadow-md"
        >
          Update Status
        </button>
      </div>
    </div>
  );
}
