import { collection, getDocs, updateDoc, doc } from "firebase/firestore";
import { firestore } from '../firebase';
import { useState, useEffect } from 'react';

export default function Mapdata() {
    const [locations, setLocations] = useState([]);
    const [inputValue, setInputValue] = useState("");

    const fetchLocations = async () => {
        const querySnapshot = await getDocs(collection(firestore, "locations"));
        const locationsData = querySnapshot.docs.map(doc => ({ ...doc.data(), id: doc.id }));
        setLocations(locationsData);
        console.log(locationsData);
    };

    useEffect(() => {
        fetchLocations();
    }, []);

    const handleClick = (location) => {
        window.location.href = location.lola;
    };

    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const locationToUpdate = locations.find((location) => location.id === "locationId");
        if (locationToUpdate) {
            await updateDoc(doc(firestore, "locations", "locationId"), {
                condition: inputValue ? "normal" : "full"
            });
            setInputValue("");
            fetchLocations();
        }
    };

    return (
        <div>
            <h1>Locations:</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" value={inputValue} onChange={handleInputChange} />
                <button type="submit">Update</button>
            </form>
            <ul>
                {locations.map(location => (
                    <li key={location.id} onClick={() => handleClick(location)}>
                        {location.name} {location.condition}
                    </li>
                ))}
            </ul>
        </div>
    );
}
