import "firebase/firestore"
import { collection, getDocs } from "firebase/firestore";
import { firestore } from '../firebase';
import { useState, useEffect } from 'react';

const db = firestore();

export default function Mapdata() {
    const [locations, setLocations] = useState([]);
    const [number, setNumber] = useState("");
    const [condition, setCondition] = useState("");

    const fetchLocations = async () => {
        const querySnapshot = await getDocs(collection(firestore(), "locations"));
        const locationsData = querySnapshot.docs.map(doc => ({ ...doc.data(), id: doc.id }));
        setLocations(locationsData);
        console.log(locationsData);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // update Firestore document with entered number and change "condition" to "normal"
        db.collection("locations").doc("locations").set({
            number: number,
            condition: "normal",
        });
    };

    const handleClick = (location) => {
        window.location.href = location.lola;
    };

    // fetch "condition" value from Firestore document on component mount
    useEffect(() => {
        db.collection("locations")
            .doc("locations")
            .onSnapshot((doc) => {
                setCondition(doc.data().condition);
            });
    }, []);

    useEffect(() => {
        fetchLocations();
    }, []);

    return (
        <div>
            <h1>Locations:</h1>
            <ul>
                {locations.map(location => (
                    <li key={location.id} onClick={() => handleClick(location)}>
                        {location.name} {location.condition}
                    </li>
                ))}
            </ul>

            <form onSubmit={handleSubmit}>
                <label>
                    Enter a number:
                    <input
                        type="number"
                        value={number}
                        onChange={(event) => setNumber(event.target.value)}
                    />
                </label>
                <button type="submit">Submit</button>
            </form>
            <p>Condition: {condition}</p>
        </div>
    );
}
